package com.ling.bobozai.galleryfinal_android.unitls;import android.app.Activity;import android.widget.AbsListView;import com.bumptech.glide.Glide;/** * Created by boboz on 2018/3/24. */public class GlidePauseOnScrollListener extends MyPauseOnScrollListener {    public GlidePauseOnScrollListener(Activity activity, boolean pauseOnScroll, boolean pauseOnFling) {        super(activity, pauseOnScroll, pauseOnFling);    }    public GlidePauseOnScrollListener(Activity activity, boolean pauseOnScroll, boolean pauseOnFling, AbsListView.OnScrollListener customListener) {        super(activity, pauseOnScroll, pauseOnFling, customListener);    }    @Override    public void resume() {        Glide.with(getActivity()).resumeRequests();    }    @Override    public void pause() {        Glide.with(getActivity()).pauseRequests();    }}